import { useState } from 'react'
import Card from '../UI/Card'
import styles from './UserList.module.css'
import Button from '../UI/Button'
import ErrorModal from '../UI/ErrorModal'

const UserList = (props) => {
	const [userDelete, setUserDelete] = useState(null)
    const [userId, setUserId ] = useState('')

	const showModalHandler = (event) => {
        setUserId
		setUserDelete(true)
	}

    const closeModalHandler = () =>{
        setUserDelete(null)
    }

    const removeInput = (event) =>{
        const filteredUsers = props.users.filter(el=> el.id !== event.target.id)
        props.deleteUsers([...filteredUsers])
        setUserDelete(null)
        
    }
    
	return (
		<div>
            {userDelete && (
				<ErrorModal
					title= 'Удаление пользователя!'
					message= 'Вы действительно хотите удалить?'
					onConfirm={removeInput}
				> <Button onClick={closeModalHandler}>Cancel</Button></ErrorModal>
			)}
			<Card className={styles.users}>
				<ul>
					{props.users.map((user) => (
						<li key={user.id} id={user.id}>
							{user.name} ({user.age} years old)
							<Button onClick={showModalHandler}>delete</Button>
						</li>
					))}
				</ul>
			</Card>
		</div>
	)
}

export default UserList
